import React, { useContext, useState, useEffect } from 'react';
import { PersonContext } from '../contexts/PersonContext';
import { Dialog } from "primereact/dialog";
import { Button } from "primereact/button";
import { Dropdown } from "primereact/dropdown";
import { InputText } from "primereact/inputtext";


const PersonDetail = (props) => {

    const { isVisibleDetails, setIsVisibleDetails } = props;

    const close = () => {
        setIsVisibleDetails(false);
      };

    return (

        <div>
            <Dialog
                visible={isVisibleDetails}
                modal={true}
                style={{ width: "420px" }}
                contentStyle={{ overflow: "visible" }}
                header="Person example"
                onHide={() => close()}
                footer={dialogFooter}
            >
                <div className="p-grid p-fluid">
                    <br />
                    <div className="p-float-label">
                        <InputText
                            value={personData.name}
                            onChange={(e) => updateField(e.target.value.trim(), "name")}
                        />
                        <label>Name:</label>
                    </div>
                    <br />
                    <div className="p-float-label">
                        <InputText
                            value={personData.age}
                            onChange={(e) => updateField(e.target.value.trim(), "age")}
                        />
                        <label>Age:</label>
                    </div>
                    <br />
                    <br />
                    <div className="p-float-label">
                        {/* <InputText
                            value={personData.personTypeId}
                            onChange={(e) => updateField(e.target.value.trim(), "personTypeId")}
                        /> */}
                        <Dropdown optionLabel="description" optionValue="id" value={typeSelected} options={personTypes} onChange={(e) => updateField(e.target.value, "personTypeId")} placeholder="Select a Person Type" />
                    </div>
                    <br />
                </div>
            </Dialog>

        </div>);
}

export default PersonDetail;
