import React, { useContext, useState, useEffect, useMemo } from 'react';
import { Panel } from 'primereact/panel';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { PersonContext } from "../contexts/PersonContext";
import PersonForm from './PersonForm';


const PersonList = () => {

    const { persons, findPerson, personTypes } = useContext(PersonContext);
    const [isVisible, setIsVisible] = useState(false);

    useEffect(() => {
        console.log(persons)
        console.log(personTypes)
    });

    const savePerson = (id) => {
        findPerson(id);
        setIsVisible(true);
    };

    const footer = (
        <div className="p-clearfix" style={{ width: "100%" }}>
            <Button style={{ float: "left" }}
                icon="pi pi-plus"
                label="Add New Person"
                onClick={() => setIsVisible(true)}
            ></Button>
        </div>
    );
   
    const personstable = useMemo(() => {
        let personarray = []

       if( personTypes &&persons )
       {
        personarray = persons.map(item => {
            console.log(personTypes)
            return ({
                ...item,
                personTypeId: personTypes.find(x => x.id === 1).description
            }
            )
        });
        }
        return personarray;
    }, [persons, personTypes]);




    return (
        <div>
            <Panel header="Person List" style={{ textAlign: "center" }}>
                <DataTable
                    value={personstable}
                    selectionMode="single"
                    onSelectionChange={(e) => savePerson(e.value.id)}
                    footer={footer}>

                    <Column field="id" header="Id"></Column>
                    <Column field="name" header="Name"></Column>
                    <Column field="age" header="Age"></Column>
                    <Column field={"personTypeId"} header="Person Type"></Column>
                </DataTable>
            </Panel>
            <PersonForm isVisible={isVisible} setIsVisible={setIsVisible}></PersonForm>
        </div>
    );
}

export default PersonList;
