import axios from "axios";



export class PersonService {

    baseUrl = "http://localhost:57637/person/";

    async createPerson(person) {
        var personcontent = {
            "name": person.name,
            "age": parseInt(person.age),
            "persontypeid": parseInt(person.personTypeId)
        }
        var result = await axios.post(this.baseUrl + "createperson", personcontent);
        return result.data.data[0];
    }

   async deletePerson(personid) {
        debugger
        var result = await axios.delete(this.baseUrl + "deleteperson?"+ personid);
        return result;
    }

    getPersons() {
        return axios.get(this.baseUrl + "getpersons").then(res => res.data);
    }

    updatePerson(person) {
        var personcontent = {
            "id" : person.id,
            "name": person.name,
            "age": parseInt(person.age),
            "persontypeid": parseInt(person.personTypeId)
        }
        return axios.put(this.baseUrl + "updateperson", personcontent).then(res => res.data);
    }

    getPersonTypes() {
        return axios.get(this.baseUrl + "getpersontypes").then(res => res.data);

    }

}
