import axios from "axios";



export class PersonService {

    baseUrl = "http://localhost:57637/person/";

    async createPerson(person) {
        var persontest = {
            "name": person.name,
            "age": parseInt(person.age),
            "persontypeid": parseInt(person.personTypeId)
        }
        var result = await axios.post(this.baseUrl + "createperson", persontest);
        return result.data.data[0];
    }

    deletePerson(personid) {
        return axios.delete(this.baseUrl + "deleteperson", personid).then(res => res.data);
    }

    getPersons() {
        return axios.get(this.baseUrl + "getpersons").then(res => res.data);
    }

    updatePerson(person) {
        return axios.put(this.baseUrl + "updateperson", person).then(res => res.data);
    }

    getPersonTypes() {
        return axios.get(this.baseUrl + "getpersontypes").then(res => res.data);

    }

}
