import React, { useContext, useState, useEffect } from 'react';
import { PersonContext } from '../contexts/PersonContext';
import { Dialog } from "primereact/dialog";

const PersonForm = (props) => {

    const { iVisible, setIsVisible } = props;

    const {
        createPerson,
        deletePerson,
        updatePerson
    } = useContext(PersonContext)

    const initialPersonState = {
        id: null,
        name: "",
        age: 0,
        personTypeId: 0
    }

    return (<div>
        <Dialog
            visible={isVisible}
            modal={true}
            style={{ width: "420px" }}
            contentStyle={{ overflow: "visible" }}
            header="Detalles del Producto"
            onHide={() => clearSelected()}
            footer={dialogFooter}
        >
            <div className="p-grid p-fluid">
                <br />
                <div className="p-float-label">
                    <InputText
                        value={productData.name}
                        onChange={(e) => updateField(e.target.value.trim(), "name")}
                    />
                    <label>Nombre:</label>
                </div>
                <br />
                <div className="p-float-label">
                    <InputNumber
                        value={productData.price}
                        onChange={(e) => updateField(e.target.value, "price")}
                        mode="currency"
                        currency="USD"
                    />
                    <label>Precio:</label>
                </div>
                <br />
                <div className="p-float-label">
                    <Calendar
                        value={
                            productData.expiry_date &&
                            new Date(productData.expiry_date + " ")
                        }
                        onChange={(e) =>
                            updateField(
                                e.target.value.toISOString().substring(0, 10),
                                "expiry_date"
                            )
                        }
                        dateFormat="yy-mm-dd"
                    />
                    <label>Fecha de caducidad:</label>
                </div>
                <br />
            </div>
        </Dialog>

    </div>);
}

export default PersonForm;
