import React, { useContext, useState, useEffect } from 'react';
import { PersonContext } from '../contexts/PersonContext';
import { Dialog } from "primereact/dialog";
import { Button } from "primereact/button";
import { InputText } from "primereact/inputtext";
import { InputNumber } from "primereact/inputnumber";

const PersonForm = (props) => {

    const { isVisible, setIsVisible } = props;

    const {
        createPerson,
        deletePerson,
        updatePerson,
        editPerson
    } = useContext(PersonContext)

    const initialPersonState = {
        id: null,
        name: "",
        age: 0,
        personTypeId: 0
    };

    const [personData, setPersonData] = useState(initialPersonState);

    useEffect(() => {
        if (editPerson) setPersonData(editPerson);
      }, [editPerson]);

    const updateField = (data, field) => {
        setPersonData({
          ...personData,
          [field]: data,
        });
    
        console.log(personData);
      };

    const _deletePerson = () => {
        if (editPerson) {
        deletePerson(personData.id);
        setPersonData(initialPersonState);
        }
        setIsVisible(false);
      };

    const savePerson = () => {
        if (!editPerson) {
            debugger
          createPerson(personData);
        } else {
          updatePerson(personData);
        }
        setPersonData(initialPersonState);
        setIsVisible(false);
      };

    const dialogFooter = (
        <div className="ui-dialog-buttonpane p-clearfix">
          <Button label="Delete" icon="pi pi-times" onClick={_deletePerson} />
          <Button label="Save" icon="pi pi-check" onClick={savePerson} />
        </div>
      );

    const clearSelected = () => {
        setIsVisible(false);
        setPersonData(initialPersonState);
      };

    return (<div>
        <Dialog
            visible={isVisible}
            modal={true}
            style={{ width: "420px" }}
            contentStyle={{ overflow: "visible" }}
            header="Person Details"
            onHide={() => setIsVisible(false)}
            footer={dialogFooter}
        >
            <div className="p-grid p-fluid">
                <br />
                <div className="p-float-label">
                    <InputText
                        value={personData.name}
                        onChange={(e) => updateField(e.target.value.trim(), "name")}
                    />
                    <label>Name:</label>
                </div>
                <br />
                <div className="p-float-label">
                    <InputText
                        value={personData.age}
                        onChange={(e) => updateField(e.target.value.trim(), "age")}
                    />
                    <label>Age:</label>
                </div>
                <br />
                <div className="p-float-label">
                    <InputText
                        value={personData.personTypeId}
                        onChange={(e) => updateField(e.target.value.trim(), "personTypeId")}
                    />
                    <label>Person Type:</label>
                </div>
                <br />
            </div>
        </Dialog>

    </div>);
}

export default PersonForm;
