import React, { useContext, useState, useEffect, useMemo } from 'react';
import { Panel } from 'primereact/panel';
import { DataTable } from 'primereact/datatable';
import { Column } from 'primereact/column';
import { Button } from 'primereact/button';
import { PersonContext } from "../contexts/PersonContext";
import PersonForm from './PersonForm';
import PersonDetail from './PersonDetail';



const PersonList = () => {

    
    const { persons, findPerson, personTypes } = useContext(PersonContext);
    const [isVisible, setIsVisible] = useState(false);

    const savePerson = (id) => {
        findPerson(id);
        setIsVisible(true);
    };

    const footer = (
        <div className="p-clearfix" style={{ width: "100%" }}>
            <Button style={{ float: "left" }}
                icon="pi pi-plus"
                label="Add New Person"
                onClick={() => setIsVisible(true)}
            ></Button>
        </div>
    );


    const actionBodyTemplate = (
        <Column header="Actions" style={{ textAlign: 'center', width: '6em' }} />
    );

    const personstable = useMemo(() => {
        let personarray = []
        if (personTypes && personTypes.length > 0 && persons) {
            personarray = persons.map(item => {
                return ({
                    ...item,
                    personTypeId: personTypes.find(x => x.id === item.personTypeId).description
                }
                )
            });
        }
        return personarray;
    }, [persons, personTypes]);
    return (

        <div>
            <Panel header="Person List" style={{ textAlign: "center" }}>
                {!personstable ? <h2>Loading...</h2>
                    : (
                        <DataTable
                            value={personstable}
                            selectionMode="single"
                            footer={footer}>

                            <Column field="id" header="Id" sortable></Column>
                            <Column field="name" header="Name" sortable></Column>
                            <Column field="age" header="Age" sortable></Column>
                            <Column field={"personTypeId"} header="Person Type" sortable body={<a>eheno</a>}></Column>
                            {actionBodyTemplate}
                        </DataTable>
                    )
                }
            </Panel>
            <PersonForm isVisible={isVisible} setIsVisible={setIsVisible}></PersonForm>
            <PersonDetail isVisible={isVisible} setIsVisible={setIsVisible}></PersonDetail>
        </div>
    );
}


export default PersonList;
